function App() {
  return (
    <div className="container outer">
      <h2 style={{ textAlign: "center", marginBottom:25 }}>
        Props and Lifting State Demo
      </h2>

      <div className="row">
        <div className="col-6 allTodos">
          <PersonList 
            persons={persons} 
            deletePerson={deletePerson} 
          />
        </div>
        <div className="col-5 new-todo">
          <NewPerson 
            addPerson={addPerson}
            nextPerson={newPerson}            
          />
        </div>
      </div>
    </div>
  );
}
export default App;
